import React, { Component } from 'react'
import api from '../services/api'
import axios from 'axios'



class GameScreen extends Component {
    constructor(props) {
        super(props)

        this.state = {
            heroeList: null
        }
    }

    componentDidMount() {
        api.get("/all.json")    
            .then(res => (
                (
                    this.setState({
                        heroeList: res.data
                    })
                ))
            ).catch(error => alert('erro de comunicação com o servidor ' +  error))
    }
    componentDidUpdate(){
        console.log('lista de herois ' , this.state.heroeList)
    }

    render() {
        return (
            <div>
                {this.state.heroeList !== null ? this.state.heroeList[0].name:'nao carrgado'}
            </div>
        )
    }
}

export default GameScreen
