import React from 'react';

//Material Ui
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import ListItemText from '@material-ui/core/ListItemText';
import ListItem from '@material-ui/core/ListItem';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import Box from '@material-ui/core/Box';


//Styled components
import {Title, SubTitle,StatisticTitle } from '../styles/syles'



const useStyles = makeStyles(theme => ({
    appBar: {
        position: 'relative',
    },
    title: {
        marginLeft: theme.spacing(2),
        flex: 1,
    },
}));

const Transition = React.forwardRef(function Transition(props, ref) {
    return <Slide direction="up" ref={ref} {...props} />;
});

export default function DialogStatistics(props) {
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    return (
        <div>
            <Button variant="outlined" color="primary" onClick={handleClickOpen}>
                Open full-screen dialog
      </Button>
            <Dialog fullWidth maxWidth="lg" open={props.open} onClose={handleClose} TransitionComponent={Transition}>
                <Box diplay="flex" justifyContent="center" alignItems = "center">
                    <ListItem>
                        <StatisticTitle>
                            Estatísticas
                        </StatisticTitle>
                    </ListItem>
                    <ListItem button>
                        <video style={{ width: "100%" }} src="https://preview.redd.it/sk9nb6lgccq31.gif?format=mp4&s=99acb9e5925c2b5ba8bd1f814b15c9414e56adac" className="img-fluid" />
                    </ListItem>
                    <ListItem button>
                    </ListItem>

                </Box>
            </Dialog>
        </div>
    );
}
