    underline: 'none',
    listStyle: 'none',
    color: "#1A1A64"
}
const styleButton = {
    padding: '3%',
    fontFamily: 'avengersFont',
    marginTop: "3%",
    fontSize: '4vh',
    color: 'white',
    backgroundColor : 'whitesmoke',
    textAling: 'center',
    borderRadius: '25px'
}
export default class WelcomeWindow extends Component {
    constructor(props) {
        super(props)

        this.state = {

        }
    }
    render() {
        return (
            <div

            >
                <Row className="no-gutters">
                    <img style={{ position: "fixed", width: "120%", objectFit: 'cover' }} src={AvengersBg} className="img-fluid" />
                    <Col xl={{ offset: 7, size: 5 }}>
                        <Menu>
                            <Title>
                                Hero <br />
                                App
                        </Title>

                            <Button style={styleButton}  color="secondary" > <Link to="/game" style={linkColor}> Iniciar o Jogo</Link></Button>
                            <br />

                            <Button style={styleButton} color="secondary">Sobre</Button>
                            <br />

                            <Button style={styleButton}  color="secondary"><a style={linkColor} href="http://github.com/mwerneck1956" target="_blank">GitHub </a></Button>

                        </Menu>
                    </Col>
                </Row>
            </div>
        )
    }
}
