class WelcomeBody extends Component {
    constructor(props) {
        super(props)

        this.state = {
            width : window.innerWidth , 
            height : window.innerHeight
        }
    }
    componentDidUpdate(){
        this.setState({
            width : window.innerWidth,
            height : window.innerHeight
        })
    }
    render() {
        return (
            <h1>
                Largura : {this.state.width},  
                Altura : {this.state.height}
            </h1 >
        )
    }
}

export default WelcomeBody
