import React, { Component } from 'react'
import api from '../services/api'
import axios from 'axios'
import { Card } from 'antd';

const { Meta } = Card;

const numAleatorio = () => {
    return Math.floor(Math.random() * 500)
}

class GameScreen extends Component {
    constructor(props) {
        super(props)

        this.state = {
            heroeList: null,
            numerosRepetidos: [],
        }
    }

    numAleatorio = () => {
        return Math.floor(Math.random() * 500) //Como o vetor de heroi tem tamanho 500 , pego un numero aleatorio de 0 a 499
    }

    componentDidMount() {
        api.get("/all.json")
            .then(res => (
                (
                    this.setState({
                        heroeList: res.data
                    })
                ))
            ).catch(error => alert('erro de comunicação com o servidor ' + error))
    }
    componentDidUpdate() {
        console.log('lista de herois ', this.state.heroeList)
    }

    render() {
        console.log(this.numAleatorio())
        return (
            <div>
                {this.state.heroeList ? <Card
                    hoverable
                    style={{ width: 240 }}
                    cover={<img alt="example" className="img-fluid" src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png" />}
                >
                    <Meta title="Europe Street beat" description="www.instagram.com" />
                </Card> : ''}

            </div>
        )
    }
}

export default GameScreen
