import React, { Component } from 'react'
import api from '../services/api'
import axios from 'axios'

import { Row, Col, Button } from 'reactstrap'
import { Card } from 'antd';

const { Meta } = Card;

const numAleatorio = () => {
    return Math.floor(Math.random() * 500)
}

class GameScreen extends Component {
    constructor(props) {
        super(props)

        this.state = {
            heroeList: null,
            VisitedHeroes: [],
            ActualHeroes: [],
            pickedHeroe: null
        }
    }

    numAleatorio = () => {
        return Math.floor(Math.random() * 500) //Como o vetor de heroi tem tamanho 500 , pego un numero aleatorio de 0 a 499
    }

    componentDidMount() {
        api.get("/all.json")
            .then(res => (
                (
                    this.setState({
                        heroeList: res.data
                    })
                ))
            ).catch(error => alert('erro de comunicação com o servidor ' + error))
    }
    componentDidUpdate(prevState) {
        if(this.state.heroeList && prevState.pickedHeroe !== this.state.pickedHeroe){
            this.selectHeroes()
        }

    }
    selectHeroes = () => {
        let vetHerois = [] 
        console.log('selecionei herois')
        var i = 0;
        while (i < 3) {
            let numSorteado = this.numAleatorio()

            if(i == 0){
                this.setState({pickedHeroe : this.state.heroeList[numAleatorio]})
            }

            /*
            this.setState({
                VisitedHeroes : this.state.VisitedHeroes.push(this.state.heroeList[numSorteado]) , //Guardo o id do numero sorteado para que nao se repitam cartas
                ActualHeros: this.state.ActualHeros.push( this.state.heroeList[numSorteado])
            })
            */
           vetHerois.push(this.state.heroeList.push(numAleatorio))
            i++
        }
        console.log('vetor de herois' , vetHerois)
    }
    render() {
        console.log(this.numAleatorio())
        return (
            <div>
                <Row>
                    {/*Coluna das pontuações , acertos e erros*/}
                    <Col xs={12} sm={12} xl={{ size: 4, offset: 1 }}>

                    </Col>
                    {/*Coluna das cards */}
                    <Col xl={{ size: 5 }}>
                        {this.state.pickedHeroe!==null ? <Card
                            hoverable
                            style={{ width: 300, backgroundColor: 'whitesmoke' }}
                            cover={<img alt="example" style={{ borderRadius: '15px' }} className="img-fluid" src={this.state.pickedHeroe.images.md} />}
                        >
                            <Meta title="" description="" />
                            <Button className="ml-5 " outline>{this.state.pickedHeroe.name}</Button>
                            <Button className="ml-5 " outline>Nome 1</Button>
                            <Button className="ml-5 " outline>Nome 1</Button>
                        </Card> : ''}
                    </Col>
                    {/* Coluna de cards/tempo Restante}*/}
                    <Col xl={{ size: 4 }}>

                    </Col>

                </Row>
                {/*    {this.state.heroeList ? <Card
                    hoverable
                    style={{ width: 240 }}
                    cover={<img alt="example" className="img-fluid" src= {this.state.heroeList[this.numAleatorio()].images.md } />}
                >
                    <Meta title="Europe Street beat" description="www.instagram.com" />
                </Card> : ''} */}

            </div>
        )
    }
}

export default GameScreen
